const dateInput = document.getElementById('datepicker');
const datePicker = document.getElementById('datePicker');

dateInput.addEventListener('focus', showDatePicker);

function showDatePicker() {
    datePicker.innerHTML = ''; // Clear any existing content

    const currentDate = new Date();
    const currentDay = currentDate.getDate();
    const currentMonth = currentDate.getMonth() + 1; // Months are zero-indexed
    const currentYear = currentDate.getFullYear();

    const daysInMonth = new Date(currentYear, currentMonth, 0).getDate();

    for (let day = 1; day <= daysInMonth; day++) {
        const dayDiv = document.createElement('div');
        dayDiv.textContent = day;
        dayDiv.classList.add('day');

        dayDiv.addEventListener('click', () => {
            const selectedDay = day < 10 ? `0${day}` : day;
            const selectedMonth = currentMonth < 10 ? `0${currentMonth}` : currentMonth;
            dateInput.value = `${selectedDay}/${selectedMonth}/${currentYear}`;
            hideDatePicker();
        });

        datePicker.appendChild(dayDiv);
    }

    datePicker.style.display = 'block';
}

function hideDatePicker() {
    datePicker.style.display = 'none';
}

document.addEventListener('click', (event) => {
    if (!datePicker.contains(event.target) && event.target !== dateInput) {
        hideDatePicker();
    }
});
