import React, { useState } from "react";
import ReactDOM from "react-dom";

import "./styles.css";

function App() {

  // React States
  const [errorMessages, setErrorMessages] = useState({});
  const [isSubmitted, setIsSubmitted] = useState(false);

  // User Login info
  const database = [
    {
      username: "user1",
      password: "pass1"
    },
    {
      username: "user2",
      password: "pass2"
    }
  ];

  const errors = {
    credentials: "Senha ou nome de usuário inválido"
  };

  const handleSubmit = (event) => {
    //Prevent page reload
    event.preventDefault();

    var { uname, pass } = document.forms[0];

    // Find user login info
    const userData = database.find((user) => user.username === uname.value);

    const requestOptions = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ title: 'React POST Request Example' })
    };

    fetch("https://9b0b-2804-14d-4c85-1043-6909-959d-d411-cf19.ngrok-free.app/API/SignIn",{
      method: "POST",
      headers: {
        'Content-Type': "application/json",
      },
      body: JSON.stringify({username: uname, password: pass})
    })
      .then((resp) => resp.json())
      .then((data) => {
        console.log(data);
        // Compare user info
        if (data) {
          //Change this if using passwords with salting
          if (userData.password !== pass.value) {
            // Invalid password
            setErrorMessages({ name: "credentials", message: errors.credentials });
          } else {
            setIsSubmitted(true);
          }
        } else {
          // Username not found
          setErrorMessages({ name: "credentials", message: errors.credentials });
        }
      })
      .catch((err) => console.log(err))
  };

  // Generate JSX code for error message
  const renderErrorMessage = (name) =>
    name === errorMessages.name && (
      <div className="error">{errorMessages.message}</div>
    );

  // JSX code for login form
  const renderForm = (
    <div className="form">
      <form onSubmit={handleSubmit}>
        <div className="center-text gray-text pb-1">
          Contribua e faça diferença na vida de pessoas que precisam através da doação de alimentos. Acesse sua conta para começar a ajudar!
        </div>
        <div className="input-container">
          <input placeholder="Email" type="text" name="uname" required />
          {renderErrorMessage("uname")}
        </div>
        <div className="input-container">
          <input placeholder="Senha" type="password" name="pass" required />
          {renderErrorMessage("pass")}
        </div>
        <div>
          
        </div>
        <div className="w-100 button-container">
          <input type="submit" value="Entrar"/>
        </div>
        {/* <div className="w-100 button-container">
          <input type="submit" value="Criar Conta"/>
        </div> */}
      </form>
    </div>
  );

  return (
    <div className="app">
      <div className="login-form">
        <div className="title">Login</div>
        {isSubmitted ? <div>Usuário logado com sucesso</div> : renderForm}
      </div>
    </div>
  );
}

export default App;
