import React, { useState } from "react";
import axios from 'axios';
import ReactDOM from "react-dom";

import "./styles.css";
import logo from './images/doaresLogo.png';

function App() {

  // React States
  const [errorMessages, setErrorMessages] = useState({});
  const [isLoggedIn, setisLoggedIn] = useState(false);
  const [page, setPage] = useState("login");
  const [username, setUsername] = useState("");
  const [utype, setUtype] = useState("");

  const errors = {
    r_email: "Esse email já existe",
    inv_email: "Informe um email válido",
    pass: "Informe uma senha válida"
  };

  function containsUppercase(str) {
    return /[A-Z]/.test(str);
  }

  function containsSpecialChars(str) {
    const specialChars = /[`!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?~]/;
    return specialChars.test(str);
  }

  const handleLogin = (event) => {
    event.preventDefault();

    var { email, pass } = document.forms[0];

    let uri = 'https://cda7-2804-14d-4c85-1043-3487-1a5c-6f58-482a.ngrok-free.app/API/SignIn';

    const item = {
      emailAddress: email.value,
      password: pass.value
    };
  
    fetch(uri, {
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(item)
    })
      .then((resp) => {
        if(resp.ok){
          resp.json();
          console.log(resp);
          setUsername(email.value);
          //setisLoggedIn(true);
        }else{
          var invalidEmail = false;

          if(invalidEmail){
            
          }

          console.log(resp);
          console.log(resp.statusText);
        }
      })
      .then((data) => {
        console.log(data);
      })
      .catch(error => console.error(error));


  };

  const handleRegistration = (event) => {
    event.preventDefault();

    var { email, uname, pass } = document.forms[0];

    let uri = 'https://cda7-2804-14d-4c85-1043-3487-1a5c-6f58-482a.ngrok-free.app/API/SignUp';

    const item = {
      emailAddress: email.value,
      username: uname.value,
      password: pass.value
    };
  
    setisLoggedIn(true);
    // fetch(uri, {
    //   method: 'POST',
    //   headers: {
    //     'Accept': 'application/json',
    //     'Content-Type': 'application/json',
    //   },
    //   body: JSON.stringify(item)
    // })
    //   .then((resp) => {
    //     if(resp.ok){
    //       resp.json();
    //       console.log(resp);
    //       setUsername(uname.value);
    //       //setisLoggedIn(true);
    //     }else{
    //       var invalidEmail = false;
    //       var pass8char = pass.value.length >= 8;
    //       var passSpecial = containsSpecialChars(pass.value);
    //       var passCapitalized = containsUppercase(pass.value);

    //       if(invalidEmail){
            
    //       }
    //       if(pass8char){

    //       }
    //       if(passSpecial){
            
    //       }
    //       if(passCapitalized){
            
    //       }
    //       console.log(resp);
    //       console.log(resp.statusText);
    //     }
    //   })
    //   .then((data) => {
    //     console.log(data);
    //   })
    //   .catch(error => console.error(error));


  };

  const renderErrorMessage = (name) =>
    name === errorMessages.name && (
      <div className="error">
        {errorMessages.message}
      </div>
    );

  const handleUserSelection = (event) => {
    event.preventDefault();
    setPage("selectUserType");
  };

  const login = (
    <div className="form">
      <div className="title">Login</div>
      <form onSubmit={handleLogin}>
        <div className="center-text gray-text pb-1">
          Contribua e faça diferença na vida de pessoas que precisam através da doação de alimentos. Acesse sua conta para começar a ajudar!
        </div>
        <div className="pt-5 px-10">
          <img className="w-100" src={logo}></img>
        </div>
        <div className="input-container">
          <input placeholder="Email" type="email" name="email" required />
          {renderErrorMessage("email")}
        </div>
        <div className="input-container">
          <input placeholder="Senha" type="password" name="pass" required />
          {renderErrorMessage("pass")}
        </div>
        <div className="w-100 button-container">
          <input type="submit" value="Entrar"/>
        </div>
      </form>
      <form onSubmit={handleUserSelection}>
        <div className="w-100 button-container">
          <input type="submit" value="Criar Conta"/>
        </div>
      </form>
    </div>
  );

  const selectUserType = (
    <div>
      <div className="title">Criar Conta</div>
      <form>
        <div className="w-100 button-container">
          <input type="submit" value="Sou uma cozinha solidária."/>
        </div>
      </form>
      <form>
        <div className="w-100 button-container">
          <input type="submit" value="Sou um doador."/>
        </div>
      </form>
    </div>
  );

  const registration = (
    <div className="form">
      <div className="title">Criar Conta</div>
      <form onSubmit={handleRegistration}>
        <div className="center-text gray-text pb-1">
          Contribua e faça diferença na vida de pessoas que precisam através da doação de alimentos. Crie sua conta agora mesmo!
        </div>
        <div className="pt-5 px-10">
          <img className="w-100" src={logo}></img>
        </div>
        <div className="input-container">
          <input placeholder="Nome" type="text" name="username" required />
          {renderErrorMessage("username")}
        </div>
        <div className="input-container">
          <input placeholder="Email" type="email" name="email" required />
          {renderErrorMessage("email")}
        </div>
        <div className="input-container">
          <input placeholder="Senha" type="password" name="pass" required />
          {renderErrorMessage("pass")}
        </div>
        <div className="w-100 button-container">
          <input type="submit" value="Criar Conta"/>
        </div>
      </form>
    </div>
  );

  const home = (
    <div>
      <div className="title">Home</div>
      <div>Usuário {username} logado com sucesso.</div>
      <div>Usuário logado como {utype}.</div>
      {utype === "cozinha solidária" ? (
        <div>Seu cadastro ainda não foi validado.</div>
      ) : (
        <div/>
      )}
    </div>
  );

  return (
    <div className="app">
      <div className="login-form">
        {page === "login" ? login : <div></div>}
        {page === "selectUserType" ? selectUserType : <div></div>}
        {page === "registration" ? registration : <div></div>}
        {page === "home" ? home : <div></div>}
      </div>
    </div>
  );
}

export default App;
