import React, { useState } from "react";
import axios from 'axios';
import ReactDOM from "react-dom";

import "./styles.css";
import logo from './images/doaresLogo.png';

function App() {

  // React States
  const [errorMessages, setErrorMessages] = useState({});
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [uname, setUname] = useState("");

  // User Login info

  const handleSubmit = (event) => {
    //Prevent page reload
    event.preventDefault();

    var { form_uname, pass } = document.forms[0];
    uname = form_uname.value;

    // axios.post('https://9b0b-2804-14d-4c85-1043-6909-959d-d411-cf19.ngrok-free.app/API/SignIn/test', JSON.stringify({
    //   emailAddress: 'Fred',
    //   password: 'Flintstone'
    // }))
    // .then(function (response) {
    //   console.log(response);
    // })
    // .catch(function (error) {
    //   console.log(error);
    // });

    // fetch("https://9b0b-2804-14d-4c85-1043-6909-959d-d411-cf19.ngrok-free.app/API/SignIn/test",{
    //   method: 'POST',
    //   headers: {
    //     'Content-Type': "application/json",
    //   },
    //   body: JSON.stringify({emailAddress: "test", password: "test"})
    // })
    //   .then((resp) => {
    //     console.log("test");
    //     if(resp.ok){
    //       resp.json();
    //     }else{
    //       console.log(resp.statusText);
    //     }
    //   })
      // .then((data) => {
      //   console.log(data);
      //   // Compare user info
      //   if (data) {
      //     //Change this if using passwords with salting
      //     if (userData.password !== pass.value) {
      //       // Invalid password
      //       setErrorMessages({ name: "credentials", message: errors.credentials });
      //     } else {
      //       setIsSubmitted(true);
      //     }
      //   } else {
      //     // Username not found
      //     setErrorMessages({ name: "credentials", message: errors.credentials });
      //   }
      // })
      // .catch((err) => console.log(err))

      const userData = {username: 'test', password: 'test'};

      // Compare user info
      if (userData.username === form_uname.value) {
        //Change this if using passwords with salting
        if (userData.password !== pass.value) {
          // Invalid password
          console.log("Invalid password");
          //setErrorMessages({ name: "credentials", message: errors.credentials });
        } else {
          setIsSubmitted(true);
        }
      } else {
        // Username not found
        console.log("Username not found");
        //setErrorMessages({ name: "credentials", message: errors.credentials });
      }
  };

  // Generate JSX code for error message
  const renderErrorMessage = (name) =>
    name === errorMessages.name && (
      <div className="error">
        {/* {errorMessages.message} */}
        Erro
      </div>
    );

  // JSX code for login form
  const renderForm = (
    <div className="form">
      <form onSubmit={handleSubmit}>
        <div className="center-text gray-text pb-1">
          Contribua e faça diferença na vida de pessoas que precisam através da doação de alimentos. Acesse sua conta para começar a ajudar!
        </div>
        <div className="pt-5 px-10">
          <img className="w-100" src={logo}></img>
        </div>
        <div className="input-container">
          <input placeholder="Email" type="text" name="uname" required />
          {renderErrorMessage("uname")}
        </div>
        <div className="input-container">
          <input placeholder="Senha" type="password" name="pass" required />
          {renderErrorMessage("pass")}
        </div>
        <div>
          
        </div>
        <div className="w-100 button-container">
          <input type="submit" value="Entrar"/>
        </div>
        {/* <div className="w-100 button-container">
          <input type="submit" value="Criar Conta"/>
        </div> */}
      </form>
    </div>
  );

  const mainPage = (
    <div>
      <div>Usuário {} logado com sucesso.</div>
      <div>Usuário logado como {}.</div>
    </div>
  );

  return (
    <div className="app">
      <div className="login-form">
        <div className="title">Login</div>
        {isSubmitted ? mainPage : renderForm}
      </div>
    </div>
  );
}

export default App;
