import React, { useState } from "react";
import axios from 'axios';
import ReactDOM from "react-dom";

import "./styles.css";
import logo from './images/doaresLogo.png';

function App() {

  // React States
  const [errorMessages, setErrorMessages] = useState({});
  const [isLoggedIn, setisLoggedIn] = useState(false);
  const [page, setPage] = useState("login");
  const [username, setUsername] = useState("");
  const [utype, setUtype] = useState("");
  const [navigation, setNavigation] = useState([]);

  const renderErrorMessage = (name) =>
  name === errorMessages.name && (
    <div className="error">
      {errorMessages.message}
    </div>
  );

  const errors = {
    r_email: "Esse email j치 existe",
    inv_email: "Informe um email v치lido",
    pass: "Informe uma senha v치lida"
  };

  function concatArrays(arr1, arr2){
    return arr1.concat(arr2);
  }

  function containsUppercase(str) {
    return /[A-Z]/.test(str);
  }

  function containsSpecialChars(str) {
    const specialChars = /[`!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?~]/;
    return specialChars.test(str);
  }

  function goNavigation(page){
    setPage("selectUserType");
    setNavigation(concatArrays(navigation, [page]));
  }

  function goBackNavigation(){
    var navigation_copy = navigation;
    var last_page = navigation_copy.pop();
    setNavigation(navigation_copy);
    setPage(last_page);
  }

  function clearNavigation(){
    setNavigation([]);
    if(isLoggedIn){
      goNavigation("home");
    }
    else{
      goNavigation("login");
    }
  }

  const loginCreateAccountButtonAction = (event) => {
    event.preventDefault();
    goNavigation("selectUserType");
  };

  const handleLogin = (event) => {
    event.preventDefault();

    var { email, pass } = document.forms[0];

    let uri = 'https://cda7-2804-14d-4c85-1043-3487-1a5c-6f58-482a.ngrok-free.app/API/SignIn';

    const item = {
      emailAddress: email.value,
      password: pass.value
    };

    //setisLoggedIn(true);
    //goNavigation("home");
    //setUsername(email.value);
  
    fetch(uri, {
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(item)
    })
      .then((resp) => {
        if(resp.ok){
          resp.json();
          console.log(resp);
          setUsername(email.value);
          //setisLoggedIn(true);
        }else{
          var invalidEmail = false;

          if(invalidEmail){
            
          }

          console.log(resp);
          console.log(resp.statusText);
        }
      })
      .then((data) => {
        console.log(data);
      })
      .catch(error => console.error(error));


  };

  const login = (
    <div className="form">
      <div className="title">Login</div>
      <form onSubmit={handleLogin}>
        <div className="center-text gray-text pb-1">
          Contribua e fa칞a diferen칞a na vida de pessoas que precisam atrav칠s da doa칞칚o de alimentos. Acesse sua conta para come칞ar a ajudar!
        </div>
        <div className="pt-5 px-10">
          <img className="w-100" src={logo}></img>
        </div>
        <div className="input-container">
          <input placeholder="Email" type="email" name="email" required />
          {renderErrorMessage("email")}
        </div>
        <div className="input-container">
          <input placeholder="Senha" type="password" name="pass" required />
          {renderErrorMessage("pass")}
        </div>
        <div className="w-100 button-container">
          <input type="submit" value="Entrar"/>
        </div>
      </form>
      <form onSubmit={loginCreateAccountButtonAction}>
        <div className="w-100 button-container">
          <input type="submit" value="Criar Conta"/>
        </div>
      </form>
    </div>
  );

  const toRegistrationKitchen = (event) => {
    event.preventDefault();

    setUtype("cozinha solid치ria");

    goNavigation("registration");
  };

  const toRegistrationDonor = (event) => {
    event.preventDefault();

    setUtype("doador");

    goNavigation("registration");
  };

  const selectUserType = (
    <div>
      <div className="title">Criar Conta</div>
      <div className="center-text gray-text pb-1">
        Contribua e fa칞a diferen칞a na vida de pessoas que precisam atrav칠s da doa칞칚o de alimentos. Crie sua conta agora mesmo!
      </div>
      <div className="pt-5 px-10">
        <img className="w-100" src={logo}></img>
      </div>
      <div className="pt-5"></div>
      <form onSubmit={toRegistrationKitchen}>
        <div className="w-100 button-container">
          <input type="submit" value="Sou uma cozinha solid치ria"/>
        </div>
      </form>
      <form onSubmit={toRegistrationDonor}>
        <div className="w-100 button-container">
          <input type="submit" value="Sou um doador"/>
        </div>
      </form>
    </div>
  );

  const handleRegistration = (event) => {
    event.preventDefault();

    var { email, username, pass, pass2 } = document.forms[0];

    let uri = 'https://cda7-2804-14d-4c85-1043-3487-1a5c-6f58-482a.ngrok-free.app/API/SignUp';

    const item = {
      emailAddress: email.value,
      username: username.value,
      password: pass.value
    };
  
    var pass8char = pass.value.length >= 8;
    var passSpecial = containsSpecialChars(pass.value);
    var passCapitalized = containsUppercase(pass.value);
    var pass2equals = pass.value === pass2.value;

    if(!pass8char){

    }
    if(!passSpecial){
      
    }
    if(!passCapitalized){
      
    }
    if(!pass2equals){
      
    }

    fetch(uri, {
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(item)
    })
      .then((resp) => {
        if(resp.ok){
          resp.json();
          console.log(resp);
          setUsername(username.value);
          //setisLoggedIn(true);
        }else{
          console.log(resp);
