import React, { useState } from "react";
import axios from 'axios';
import ReactDOM from "react-dom";

import "./styles.css";
import logo from './images/doaresLogo.png';

function App() {

  // React States
  const [errorMessages, setErrorMessages] = useState({});
  const [isLoggedIn, setisLoggedIn] = useState(false);
  const [page, setPage] = useState("login");
  const [username, setUsername] = useState("");
  const [utype, setUtype] = useState("");
  const [navigation, setNavigation] = useState([]);

  const errors = {
    r_email: "Esse email j치 existe",
    inv_email: "Informe um email v치lido",
    pass: "Informe uma senha v치lida"
  };

  const renderErrorMessage = (name) =>
  name === errorMessages.name && (
    <div className="error">
      {errorMessages.message}
    </div>
  );

  function concatArrays(arr1, arr2){
    return arr1.concat(arr2);
  }

  function containsUppercase(str) {
    return /[A-Z]/.test(str);
  }

  function containsSpecialChars(str) {
    const specialChars = /[`!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?~]/;
    return specialChars.test(str);
  }

  function goNavigation(page){
    setPage(page);
    setNavigation(concatArrays(navigation, [page]));
  }

  function goBackNavigation(){
    var navigation_copy = navigation;
    var last_page = navigation_copy.pop();
    setNavigation(navigation_copy);
    setPage(last_page);
  }

  function clearNavigation(){
    setNavigation([]);
    if(isLoggedIn){
      goNavigation("home");
    }
    else{
      goNavigation("login");
    }
  }

  function logout(){
    //send logout request

    setisLoggedIn(false);
    setUsername("");
    setUtype("");
    clearNavigation();
  }

  const loginCreateAccountButtonAction = (event) => {
    event.preventDefault();
    goNavigation("selectUserType");
  };

  const handleLogin = (event) => {
    event.preventDefault();

    var { email, pass } = document.forms[0];

    let uri = 'https://cda7-2804-14d-4c85-1043-3487-1a5c-6f58-482a.ngrok-free.app/API/SignIn';

    const item = {
      emailAddress: email.value,
      password: pass.value
    };

    //setisLoggedIn(true);
    //goNavigation("home");
    //setUsername(email.value);
  
    fetch(uri, {
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(item)
    })
      .then((resp) => {
        if(resp.ok){
          resp.json();
          console.log(resp);
          setUsername(email.value);
          //setisLoggedIn(true);
          //getUtype
        }else{
          var invalidEmail = false;

          if(invalidEmail){
            
