import React, { useState } from "react";
import axios from 'axios';
import ReactDOM from "react-dom";

import "./styles.css";
import logo from './images/doaresLogo.png';

function App() {

  // React States
  const [errorMessages, setErrorMessages] = useState({});
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [username, setUsername] = useState("");
  const [utype, setUtype] = useState("");

  const errors = {
    r_email: "Esse email já existe",
    inv_email: "Informe um email válido",
    pass: "Informe uma senha válida"
  };

  const handleLogin = (event) => {
    //Prevent page reload
    event.preventDefault();

    var { uname, pass } = document.forms[0];

    // check for valid email and password
    


    setUsername(uname.value);

    // axios.post('https://cda7-2804-14d-4c85-1043-3487-1a5c-6f58-482a.ngrok-free.app/API/SignIn', {
    //   emailAddress: 'Fred',
    //   password: 'Flintstone'
    // })
    // .then(function (response) {
    //   console.log(response);
    // })
    // .catch(function (error) {
    //   console.log(error);
    // });

    let uri = 'https://cda7-2804-14d-4c85-1043-3487-1a5c-6f58-482a.ngrok-free.app/API/SignIn';

    const item = {
      emailAddress: uname.value,
      password: pass.value
    };
  
    fetch(uri, {
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(item)
    })
      .then((resp) => {
        if(resp.ok){
          resp.json();
          console.log(resp);
        }else{
          console.log(resp);
          console.log(resp.statusText);
        }
      })
      .then((data) => {
        console.log(data);
      })
      .catch(error => console.error(error));

      // const userData = {username: 'test', password: 'test'};

      // // Compare user info
      // if (userData.username === uname.value) {
      //   //Change this if using passwords with salting
      //   if (userData.password !== pass.value) {
      //     // Invalid password
      //     console.log("Invalid password");
      //     //setErrorMessages({ name: "credentials", message: errors.credentials });
      //   } else {
      //     setIsSubmitted(true);
      //     setUtype("cozinha solidária");
      //   }
      // } else {
      //   // Username not found
      //   console.log("Username not found");
      //   //setErrorMessages({ name: "credentials", message: errors.credentials });
      // }
  };

  // Generate JSX code for error message
  const renderErrorMessage = (name) =>
    name === errorMessages.name && (
      <div className="error">
        {/* {errorMessages.message} */}
        Erro
      </div>
    );

  // JSX code for login form
  const renderForm = (
    <div className="form">
      <form onSubmit={handleLogin}>
        <div className="center-text gray-text pb-1">
          Contribua e faça diferença na vida de pessoas que precisam através da doação de alimentos. Acesse sua conta para começar a ajudar!
        </div>
        <div className="pt-5 px-10">
          <img className="w-100" src={logo}></img>
        </div>
        <div className="input-container">
          <input placeholder="Email" type="text" name="uname" required />
          {renderErrorMessage("uname")}
        </div>
        <div className="input-container">
          <input placeholder="Senha" type="password" name="pass" required />
          {renderErrorMessage("pass")}
        </div>
        <div>
          
        </div>
        <div className="w-100 button-container">
          <input type="submit" value="Entrar"/>
        </div>
        {/* <div className="w-100 button-container">
          <input type="submit" value="Criar Conta"/>
        </div> */}
      </form>
    </div>
  );

  const mainPage = (
    <div>
      <div>Usuário {username} logado com sucesso.</div>
      <div>Usuário logado como {utype}.</div>
      {utype === "cozinha solidária" (
        <div>Seu cadastro ainda não foi validado.</div>
      )}
    </div>
  );

  return (
    <div className="app">
      <div className="login-form">
        <div className="title">Login</div>
        {isSubmitted ? mainPage : renderForm}
      </div>
    </div>
  );
}

export default App;
